/**
 * Creates the color menu.
 */
protected JMenu createColorMenu(String title, String attribute) {
    CommandMenu menu = new CommandMenu(title);
    for (int i = 0; i < ColorMap.size(); i++) menu.add(new ChangeAttributeCommand(ColorMap.name(i), attribute, ColorMap.color(i), view()));
    return menu;
}
