/**
 * Sets the default tool of the editor.
 * @see DrawingEditor
 */
public void toolDone() {
    if (fDefaultToolButton != null) {
        setTool(fDefaultToolButton.tool(), fDefaultToolButton.name());
        setSelected(fDefaultToolButton);
    }
}
