/**
 * Save a Drawing in a file
 */
protected void saveDrawing(StorageFormat storeFormat, String file) {
    try {
        setDrawingTitle(storeFormat.store(file, drawing()));
    } catch (IOException e) {
        showStatus(e.toString());
    }
}
