/**
 * Switch to a new Look&Feel
 */
private void newLookAndFeel(String landf) {
    try {
        UIManager.setLookAndFeel(landf);
        SwingUtilities.updateComponentTreeUI(this);
    } catch (Exception e) {
        System.err.println(e);
    }
}
