/**
 * Splits the segment at the given point if a segment was hit.
 * @return the index of the segment or -1 if no segment was hit.
 */
public int splitSegment(int x, int y) {
    double dist = TOO_CLOSE;
    int best = -1;
    for (int i1 = 0; i1 < fPoly.npoints; i1++) {
        int n = (i1 + 1) % fPoly.npoints;
        double d = distanceFromLine(fPoly.xpoints[i1], fPoly.ypoints[i1], fPoly.xpoints[n], fPoly.ypoints[n], x, y);
        if (d < dist) {
            dist = d;
            best = i1;
        }
    }
    int i = best;
    if (i != -1) {
        insertPointAt(new Point(x, y), i + 1);
        return i + 1;
    } else
        return -1;
}
