public void read(StorableInput dr) throws IOException {
    super.read(dr);
    int size = dr.readInt();
    int[] xs = new int[size];
    int[] ys = new int[size];
    for (int i = 0; i < size; i++) {
        xs[i] = dr.readInt();
        ys[i] = dr.readInt();
    }
    fPoly = new Polygon(xs, ys, size);
}
