public static Point center(Polygon p) {
    long sx = 0;
    long sy = 0;
    int n = p.npoints;
    for (int i = 0; i < n; i++) {
        sx += p.xpoints[i];
        sy += p.ypoints[i];
    }
    return new Point((int) (sx / n), (int) (sy / n));
}
