public void deactivate() {
    super.deactivate();
    if (fPolygon != null) {
        fPolygon.smoothPoints();
        if (fPolygon.pointCount() < 3 || fPolygon.size().width < 4 || fPolygon.size().height < 4)
            drawing().remove(fPolygon);
    }
    fPolygon = null;
}
