public void mouseMove(MouseEvent e, int x, int y) {
    if (fPolygon != null) {
        if (fPolygon.pointCount() > 1) {
            fPolygon.setPointAt(new Point(x, y), fPolygon.pointCount() - 1);
            view().checkDamage();
        }
    }
}
