/**
 * Calculates the outline of an arrow tip.
 */
public Polygon outline(int x1, int y1, int x2, int y2) {
    double dir = Math.PI / 2 - Math.atan2(x2 - x1, y2 - y1);
    return outline(x1, y1, dir);
}
