public static void writeColor(StorableOutput dw, String colorName, Color color) {
    if (color != null) {
        dw.writeString(colorName);
        dw.writeInt(color.getRed());
        dw.writeInt(color.getGreen());
        dw.writeInt(color.getBlue());
    }
}
