public void invokeStep(int x, int y, int anchorX, int anchorY, DrawingView view) {
    int dx = x - anchorX;
    int dy = y - anchorY;
    Rectangle r = fOwner.displayBox();
    int max = r.width;
    int value = 2 * (fRadius.x + dx);
    if (value < 0)
        value = 0;
    if (value > max)
        value = max;
    int rx = value;
    int ry = Geom.range(0, r.height, 2 * (fRadius.y + dy));
    fOwner.setArc(rx, ry);
}
