private void point(int x, int y) {
    if (fScribble == null) {
        fScribble = new PolyLineFigure(x, y);
        view().add(fScribble);
    } else if (fLastX != x || fLastY != y)
        fScribble.addPoint(x, y);
    fLastX = x;
    fLastY = y;
}
