protected void endEdit() {
    if (getTypingTarget() != null) {
        if (fTextField.getText().length() > 0)
            getTypingTarget().setText(fTextField.getText());
        else {
            drawing().remove((Figure) getTypingTarget());
        }
        setTypingTarget(null);
        fTextField.endOverlay();
        view().checkDamage();
    }
}
