// -- animation support ----------------------------------------------
public void startAnimation() {
    if (drawing() instanceof Animatable && fAnimator == null) {
        fAnimator = new Animator((Animatable) drawing(), view());
        fAnimator.start();
        fAnimationButton.setText("End Animation");
    }
}
