public boolean containsPoint(int x, int y) {
    // add slop for connectors
    if (fConnectorsVisible) {
        Rectangle r = displayBox();
        int d = LocatorConnector.SIZE / 2;
        r.grow(d, d);
        return r.contains(x, y);
    }
    return super.containsPoint(x, y);
}
