public void update(FigureChangeEvent e) {
    if (e.getFigure() == figureAt(1)) {
        int newEnd = start() + duration();
        if (newEnd != end()) {
            setEnd(newEnd);
            notifyPostTasks();
        }
    }
    if (needsLayout()) {
        layout();
        changed();
    }
}
