public void updateDurations() {
    int newEnd = start() + duration();
    if (newEnd != end()) {
        setEnd(newEnd);
        Enumeration i = fPostTasks.elements();
        while (i.hasMoreElements()) ((PertFigure) i.nextElement()).updateDurations();
    }
}
