public void invokeStart(int x, int y, DrawingView view) {
    fConnection = createConnection();
    Point p = locate();
    fConnection.startPoint(p.x, p.y);
    fConnection.endPoint(p.x, p.y);
    view.drawing().add(fConnection);
}
