/**
 * Finds a connection start figure.
 */
protected Figure findConnectionStart(int x, int y, Drawing drawing) {
    Figure target = findConnectableFigure(x, y, drawing);
    if ((target != null) && target.canConnect())
        return target;
    return null;
}
