public void mouseDown(MouseEvent e, int x, int y) {
    super.mouseDown(e, x, y);
    fLastX = x;
    fLastY = y;
    if (e.isShiftDown()) {
        view().toggleSelection(fAnchorFigure);
        fAnchorFigure = null;
    } else if (!view().selection().contains(fAnchorFigure)) {
        view().clearSelection();
        view().addToSelection(fAnchorFigure);
    }
}
