/**
 * Constrains the given point.
 * @return constrained point.
 */
public Point constrainPoint(Point p) {
    p.x = ((p.x + fGridX / 2) / fGridX) * fGridX;
    p.y = ((p.y + fGridY / 2) / fGridY) * fGridY;
    return p;
}
