/**
 * Draws this connector.
 */
public void draw(Graphics g) {
    Rectangle r = displayBox();
    g.setColor(Color.blue);
    g.fillOval(r.x, r.y, r.width, r.height);
    g.setColor(Color.black);
    g.drawOval(r.x, r.y, r.width, r.height);
}
