/**
 * Adds a figure to the current selection.
 */
public void addToSelection(Figure figure) {
    if (!fSelection.contains(figure)) {
        fSelection.addElement(figure);
        fSelectionHandles = null;
        figure.invalidate();
        selectionChanged();
    }
}
