/**
 * Removes a figure from the selection.
 */
public void removeFromSelection(Figure figure) {
    if (fSelection.contains(figure)) {
        fSelection.removeElement(figure);
        fSelectionHandles = null;
        figure.invalidate();
        selectionChanged();
    }
}
