/**
 * Executes the command.
 */
public void itemStateChanged(ItemEvent e) {
    if ((getSelectedIndex() >= 0) && (getSelectedIndex() < fCommands.size())) {
        Command command = (Command) fCommands.elementAt(getSelectedIndex());
        command.execute();
    }
}
