/**
 * Returns the direction NORTH, SOUTH, WEST, EAST from
 * one point to another one.
 */
static public int direction(int x1, int y1, int x2, int y2) {
    int direction = 0;
    int vx = x2 - x1;
    int vy = y2 - y1;
    if (vy < vx && vx > -vy)
        direction = EAST;
    else if (vy > vx && vy > -vx)
        direction = NORTH;
    else if (vx < vy && vx < -vy)
        direction = WEST;
    else
        direction = SOUTH;
    return direction;
}
