/**
 * Loads an image with the given name.
 */
public Image loadImage(String filename) {
    if (fMap.containsKey(filename))
        return (Image) fMap.get(filename);
    Image image = loadImageResource(filename);
    if (image != null)
        fMap.put(filename, image);
    return image;
}
