package CH.ifa.draw.util;

import javax.swing.*;
import java.awt.*;
import java.awt.event.*;

/**
 * A palette button is a three state button. The states are normal
 * pressed and selected. It uses to the palette listener interface
 * to notify about state changes.
 *
 * @see PaletteListener
 * @see PaletteLayout
 */
public abstract class PaletteButton extends JButton implements MouseListener, MouseMotionListener {

    static final int NORMAL = 1;

    static final int PRESSED = 2;

    static final int SELECTED = 3;

    private PaletteListener fListener;

    private int fState;

    private int fOldState;

    public PaletteButton(PaletteListener listener) {
        fListener = listener;
        fState = fOldState = NORMAL;
        addMouseListener(this);
        addMouseMotionListener(this);
    }

    public Object value() {
        return null;
    }

    public String name() {
        return "";
    }

    public void reset() {
        fState = NORMAL;
        setSelected(false);
        repaint();
    }

    public void select() {
        fState = SELECTED;
        setSelected(true);
        repaint();
    }

    public void mousePressed(MouseEvent e) {
        fOldState = fState;
        fState = PRESSED;
        repaint();
    }

    public void mouseDragged(MouseEvent e) {
        if (contains(e.getX(), e.getY()))
            fState = PRESSED;
        else
            fState = fOldState;
        repaint();
    }

    public void mouseReleased(MouseEvent e) {
        fState = fOldState;
        repaint();
        if (contains(e.getX(), e.getY()))
            fListener.paletteUserSelected(this);
    }

    public void mouseMoved(MouseEvent e) {
        fListener.paletteUserOver(this, true);
    }

    public void mouseExited(MouseEvent e) {
        if (// JDK1.1 on MS-Windows sometimes looses mouse released
        fState == PRESSED)
            mouseDragged(e);
        fListener.paletteUserOver(this, false);
    }

    public void mouseClicked(MouseEvent e) {
    }

    public void mouseEntered(MouseEvent e) {
    }
