/**
 * Reads and resurrects a Storable object from the input stream.
 */
public Storable readStorable() throws IOException {
    Storable storable;
    String s = readString();
    if (s.equals("NULL"))
        return null;
    if (s.equals("REF")) {
        int ref = readInt();
        return (Storable) retrieve(ref);
    }
    storable = (Storable) makeInstance(s);
    map(storable);
    storable.read(this);
    return storable;
}
