        fStream.print("NULL");
        space();
        return;
    }
    if (mapped(storable)) {
        int ref = fMap.indexOf(storable);
        fStream.print("REF");
        space();
        fStream.print(ref);
        space();
        return;
    }
    incrementIndent();
    startNewLine();
    map(storable);
    fStream.print(storable.getClass().getName());
    space();
