     * Writes a storable object to the output stream.

public void writeStorable(Storable storable) {
        if (storable == null) {
            fStream.print("NULL");
            space();
            return;
        }

        if (mapped(storable)) {
            int ref = fMap.indexOf(storable);

			fStream.print("REF");
			space();
			fStream.print(ref);
			space();
            return;
        }

        incrementIndent();
        startNewLine();
        map(storable);
        fStream.print(storable.getClass().getName());
        space();
        storable.write(this);
        space();
        decrementIndent();
    }

