public String intercept(ActionInvocation actionInvocation) throws Exception {
    // f:log
    logger.info("Starting verifying user");
    Map session = actionInvocation.getInvocationContext().getSession();
    User user = (User) session.get(SystemConstants.AUTHENTICATED_USER);
    boolean isAuthenticated = (null != user);
    if (isAuthenticated) {
        // f:log
        logger.info("Finishing verifying user -- verified");
        return actionInvocation.invoke();
    }
    // f:log
    logger.info("Finishing verifying user -- unverified");
    return Action.LOGIN;
}
