public Image loadImage(String name) {
    Toolkit toolkit = Toolkit.getDefaultToolkit();
    try {
        URL url = BaseTestRunner.class.getResource(name);
        return toolkit.createImage((ImageProducer) url.getContent());
    } catch (Exception ex) {
    }
    return null;
}
