Hashtable collectFilesInRoots(Vector roots) {
    Hashtable result = new Hashtable(100);
    Enumeration e = roots.elements();
    while (e.hasMoreElements()) gatherFiles(new File((String) e.nextElement()), "", result);
    return result;
}
