public StatusLine(int preferredWidth) {
    super();
    setFont(BOLD_FONT);
    setEditable(false);
    setBorder(BorderFactory.createBevelBorder(BevelBorder.LOWERED));
    Dimension d = getPreferredSize();
    d.width = preferredWidth;
    setPreferredSize(d);
}
