protected JLabel createLogo() {
    JLabel label;
    Icon icon = getIconResource(BaseTestRunner.class, "logo.gif");
    if (icon != null)
        label = new JLabel(icon);
    else
        label = new JLabel("JV");
    label.setToolTipText("JUnit Version " + Version.id());
    return label;
}
