synchronized protected void runTest(final Test testSuite) {
    if (fRunner != null) {
        fTestResult.stop();
    } else {
        /*{*/
        fCounterPanel.reset();
        fProgressIndicator.reset();
        fRerunButton.setEnabled(false);
        fFailureView.clear();
        fFailures.clear();
        /*}*/
        if (testSuite != null) {
            doRunTest(testSuite);
        }
    }
}
