public static int getFilesCitySize(int projectid) throws SQLException {
    int size = 3;
    String sqlStmt = QueryBuilder.selectFilesCitySize(projectid);
    ResultSet res = null;
    res = statement.executeQuery(sqlStmt);
    while (res.next()) {
        size += res.getInt("size");
    }
    if (res != null)
        res.close();
    return size;
}
