public static boolean matchesWord(String word, String keyword) {
    keyword = keyword.toLowerCase();
    String wordLowerCase = word.toLowerCase();
    if (wordLowerCase.contains(keyword) || keyword.contains(wordLowerCase)) {
        return true;
    } else {
        return false;
    }
}
