private void notifyUsers(IArtifact artifact1, IArtifact artifact2) {
    for (User user : artifact1.getAssociatedUsers()) {
        if (user != null) {
            if (!containsArtifact(user, artifact2)) {
                user.addRelatedArtifacts(artifact2, 1);
                usersChanged.add(user);
            }
        }
    }
}
