public int getNumberOfRevisionsBeforeDate(Timestamp timestamp) {
    int count = 0;
    for (WikiRevision rev : revisions) {
        if (rev.getRev_timestamp().before(timestamp)) {
            count++;
        }
    }
    return count;
}
