public static void printCityBlocks(ArrayList<CityBlock> blocks, HashMap<SVNFile, Integer> fileCount) throws IOException {
    // BufferedWriter out = new BufferedWriter(new
    // FileWriter("C:\\Documents and Settings\\Marios Fokaefs\\Desktop\\wikidev-files\\ucosp\\clusters"+week+".txt"));
    BufferedWriter out = new BufferedWriter(new FileWriter("C:\\Documents and Settings\\Marios Fokaefs\\Desktop\\wikidev-files\\c301f09\\industrialBlocks.txt"));
    int index = 1;
    out.write("" + Math.sqrt(blocks.size()));
    out.newLine();
    for (CityBlock block : blocks) {
        for (IArtifact artifact : block.getArtifacts().keySet()) {
            String type = "" + index + "\t";
            int height = 0;
            String line = "";
            if (artifact instanceof SVNFile) {
                SVNFile file = (SVNFile) artifact;
                type += file.getType();
                height = fileCount.get(file);
                line += type + "\t" + block.getIndex().x + "\t" + block.getIndex().y + "\t" + block.getArtifacts().get(artifact) + "\t" + height + "\t";
                for (User user : file.getUsers()) {
                    line += "<" + user.getUserName() + ", " + user.getColor().getRed() + ", " + user.getColor().getGreen() + ", " + user.getColor().getBlue() + "> \t";
                }
            }
            out.write(line);
            out.newLine();
            index++;
        }
    }
    out.close();
}
