private void cityBlockInitialization(ArrayList<IArtifact> newArtifacts, double[][] coords, double minX, double minY, double range, double blocks) {
    this.cityBlocks = new ArrayList<CityBlock>();
    for (int i = 0; i < blocks; i++) {
        for (int j = 0; j < blocks; j++) {
            double[] center = { minX + i * (range / 16) + (range / 16) / 2, minY + j * (range / 16) + (range / 16) / 2 };
            CityBlock cityblock = new CityBlock(new Point(i, j));
            cityblock.setCenter(center);
            cityBlocks.add(cityblock);
        }
    }
    double r = range / blocks;
    for (int i = 0; i < newArtifacts.size(); i++) {
        assignArtifactToCityBlock(newArtifacts.get(i), coords[i][0], coords[i][1], minX, minY, blocks, r);
    }
}
