private void assignArtifactToCityBlock(IArtifact artifact, double x, double y, double minX, double minY, double blocks, double r) {
    int blockX = 0;
    int blockY = 0;
    for (int i = 1; i < blocks; i++) {
        if (x > minX + (i - 1) * r && x <= minX + i * r) {
            blockX = i - 1;
        }
        if (y > minY + (i - 1) * r && y <= minY + i * r) {
            blockY = i - 1;
        }
    }
    CityBlock block = getCityBlock(new Point(blockX, blockY));
    block.addArtifact(artifact, -1);
}
