public static void FisherYatesShuffle(int[] array) {
    for (int i = array.length - 1; i > 0; i--) {
        // Pick random positoin:
        int pos = _rnd.nextInt(i + 1);
        // Swap:
        int tmp = array[i];
        array[i] = array[pos];
        array[pos] = tmp;
    }
}
