private static void dfs(int[] component, int v, int cn) {
    component[v] = cn;
    for (int w = 0; w < adjacencyMatrix[v].length; w++) {
        if (adjacencyMatrix[v][w] != 0)
            if (component[w] == 0)
                dfs(component, w, cn);
    }
}
