public static double getMean(double[] vector) {
    double sum = 0;
    double mean = 0;
    for (int i = 0; i < vector.length; i++) {
        sum += vector[i];
    }
    if (vector.length > 0) {
        mean = sum / vector.length;
    }
    return mean;
}
