public static double getStandardDeviation(double[] vector) {
    double mean = getMean(vector);
    double sum = 0;
    double sd = 0;
    for (int i = 0; i < vector.length; i++) {
        sum += Math.pow((vector[i] - mean), 2);
    }
    if (vector.length > 0) {
        sd = Math.sqrt(sum / vector.length);
    }
    return sd;
}
