		for(User user : artifact.getAssociatedUsers()) {
			if (user != null) {
				if (!containsArtifact(user, artifact)) {
					user.addRelatedArtifacts(artifact, 0);
					usersChanged.add(user);
				}
			}
		}
		project.addRelatedArtifact(artifact);
		projectsChanged.add(project);
	}
